import {AfterViewInit, Component, ViewChild} from '@angular/core';
import {MatPaginator} from "@angular/material/paginator";
import {MatSort} from "@angular/material/sort";
import {MatTable} from "@angular/material/table";
import {DataSourceType, MockInterfaceResultData} from "../../ssl-identy/ssl-identy-interface";
import {MockDataDiaLogAEAI, WRData, WRBaseData, random} from "./dialog-ai-estimate.interface";

@Component({
  selector: 'app-dialog-ai-estimate',
  templateUrl: './dialog-ai-estimate.component.html',
  styleUrls: ['./dialog-ai-estimate.component.scss']
})
export class DialogAiEstimateComponent {
  @ViewChild(MatPaginator) paginator!: MatPaginator;
  @ViewChild(MatSort) sort!: MatSort;
  @ViewChild(MatTable) table!: MatTable<WRData>;

  dataSource: WRData[] = [];
  dataStructure = [
    {key: "内容", value: "Content", width: "auto"},
    {key: "类型", value: "Type", width: "70px"},
    {key: "发送时间", value: "SendTime", width: "180px"},
  ];

  /** Columns displayed in the table. Columns IDs can be added, removed, or reordered. */
  columnsToDisplay = ['content', 'Type', 'completed_at'];//, 'msgDetails'
  displayDic = {
    content:{key: "内容", width: "auto"},
    Type:{key: "类型", width: "70px"},
    completed_at:{key: "发送时间", width: "220px"},
  }
  columnsToDisplayWithExpand = ['select',...this.columnsToDisplay, 'expand'];

  constructor() {

    let baseData:WRBaseData[] = MockDataDiaLogAEAI;
    let data:WRData[] = <WRData[]>baseData;

    let arr:any = ["小明","小黄","小率","小点","小笑"]


    data.forEach(objc => {
      objc.completed = false;
      objc.type = random( 1,2).toString();
      objc.typeStr = objc.type=="1"?"信息":"语音";
    })
    this.dataSource = data
  }
  ordering = '-created_at';
  //选中的id
  selectID:String = "";
  sortData(value: string) {
    if(this.ordering.includes(value)) {
      this.ordering = `-created_at`
    }else {
      this.ordering = `${value},${this.ordering}`
    }

  }

  ngAfterViewInit(): void {

  }

  selectCell(id:string){
    console.log("id=====", id);
    if (this.selectID == id){
      this.selectID = "";
    }else{
      this.selectID = id;
    }
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {


  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    let newDatas = this.dataSource.filter(objc => objc.completed == false )
    return newDatas.length == 0;
  }
  isIndeterminate(){
    let newDatas = this.dataSource.filter(objc => objc.completed == true)
    //这种情况说明选中了但是 没有全部选中
    return newDatas.length != 0 && newDatas.length != this.dataSource.length;
  }

  setAllSelected() {
    let newDatas = this.dataSource.filter(objc => objc.completed == true )
    //如果 没有选中的 或者没有选中全部(也就是说选中的数量小于全部数据)
    if (newDatas.length < this.dataSource.length){
      this.dataSource.forEach(objc => objc.completed = true)
    }else {
      //选中的全部数据,那么取消选中
      this.dataSource.forEach(objc => objc.completed = false)
    }

  }

  clickSub(){
    console.log("dataSource===", this.dataSource);


  }


}

<ng-container class="root-test">
    <div class="mat-elevation-z8">
        <perfect-scrollbar>
            <div class="table-box">
                <mat-table
                       [dataSource]="dataSource" multiTemplateDataRows>
                    <ng-container matColumnDef="select">
                        <th mat-header-cell *matHeaderCellDef style="min-width: 64px;">
                            <mat-checkbox
                                    [checked]="isAllSelected()"
                                    (click)="setAllSelected()"
                                    [indeterminate]="isIndeterminate()">
                            </mat-checkbox>
                        </th>
                        <td mat-cell *matCellDef="let row" style="min-width: 64px;">
                            <!--
                            checked设置选中状态
                            click拦截点击事件
                            -->
                            <mat-checkbox *ngIf="selectID!=row.id"
                                          [checked]="row.completed"
                                          (click)="$event.stopPropagation();row.completed = !row.completed;clickSub()">
                            </mat-checkbox>
                        </td>
                    </ng-container>
                    <div  matColumnDef="{{column}}" *ngFor="let column of columnsToDisplay">
                        <ng-container  *ngIf="column=='content'" [style.width]="displayDic.content.width">
                            <th mat-header-cell  *matHeaderCellDef [style.width]="displayDic.content.width">
                                <div class="ellipsis">内容</div>
                            </th>
                            <td mat-cell *matCellDef="let element">
                                <div  *ngIf="selectID!=element.id" class="ellipsis">{{element.content}}</div>
                            </td>
                        </ng-container>
                        <ng-container *ngIf="column=='Type'" [style.width]="displayDic.Type.width">
                            <th mat-header-cell *matHeaderCellDef [style.width]="displayDic.Type.width"> 类型</th>
                            <td mat-cell *matCellDef="let element" [style.width]="displayDic.Type.width">
                                <div *ngIf="selectID!=element.id">
                                    <div *ngIf="element.type == '1'">
                                        信息
                                    </div>
                                    <div *ngIf="element.type == '2'">
                                        语音
                                    </div>
                                </div>
                            </td>
                        </ng-container>
                        <div *ngIf="column=='completed_at'" [style.width]="displayDic.completed_at.width">
                            <th mat-header-cell *matHeaderCellDef [style.width]="displayDic.completed_at.width"> 发送时间
                            </th>
                            <td mat-cell *matCellDef="let element"
                                [style.width]="displayDic.completed_at.width">
                                <div *ngIf="selectID!=element.id">
                                    {{element.completed_at | date:'yyyy-MM-dd HH:mm:ss'}}
                                </div>
                            </td>
                        </div>
                    </div>
                    <ng-container matColumnDef="expand" style="width: 190px;">
                        <th mat-header-cell *matHeaderCellDef>是否WR?</th>
                        <td mat-cell *matCellDef="let row" style="color:#999;" (click)="selectCell(row.id)">
                            <div *ngIf="selectID!=row.id" class="cell-handle ellipsis">
                                <ng-container>
                                    <!-- 提示 -->
                                    <button class="yesBtn" mat-raised-button color="warn">
                                        {{ '是' }}
                                    </button>
                                    <button class="noBtn" mat-raised-button>
                                        {{ '否' }}
                                    </button>
                                </ng-container>
                            </div>
                        </td>
                    </ng-container>

                    <ng-container matColumnDef="expandedDetail">
                        <td mat-cell *matCellDef="let element" [attr.colspan]="columnsToDisplayWithExpand.length">
                            <div *ngIf="selectID == element.id" class="example-element-detail">
                                <mat-checkbox [checked]="element.completed"
                                              (click)="$event.stopPropagation();element.completed = !element.completed;clickSub()"
                                              style="min-width: 64px;">
                                </mat-checkbox>
                                <app-msg-details-show [msgType]="element.type" [msgText]="element.content"  (closeContent)="selectID=''"></app-msg-details-show>
                            </div>
                        </td>
                    </ng-container>

                    <tr mat-header-row *matHeaderRowDef="columnsToDisplayWithExpand;sticky: true"></tr>
                    <tr mat-row *matRowDef="let element; columns: columnsToDisplayWithExpand;"
                        class="example-element-row"
                        [ngStyle]="{'height': (element.id===selectID ? 0 :50)+'px'}"
                        [class.example-expanded-row]="selectID === element.id"
                        (click)="selectID = selectID === element.id ? '' : element.id"
                    ></tr>
                    <tr mat-row *matRowDef="let row; columns: ['expandedDetail']" class="example-detail-row"></tr>
                </mat-table>

                <div class="tf-test">


                    <mat-paginator [length]="dataSource.length"
                                   [pageIndex]="0"
                                   [pageSize]="10"
                                   aria-label="Select page">
                    </mat-paginator>
                </div>

            </div>
        </perfect-scrollbar>
    </div>
</ng-container>
