import {Component, EventEmitter, OnInit, Output} from '@angular/core';
import { Router } from '@angular/router';
import {SidebarAnimation} from "./ssl-sidebar.animation";

// 菜单结构
interface SidebarMenu {
  link: string;
  text: string;
  icon: string;
  jview: boolean;
  children?: any;
  isOpen?: boolean;
  [propName: string]: any;
}
@Component({
  selector: 'app-ssl-sidebar',
  templateUrl: './ssl-sidebar.component.html',
  styleUrls: ['./ssl-sidebar.component.scss'],
  animations:SidebarAnimation,
})
export class SslSidebarComponent implements OnInit {

  routerPath = '';
  // 侧边栏导航菜单
  sidebarMenu: SidebarMenu[];

  JmanageData = true;
  // 用户
  JmanageUser = true;
  // app
  JmanageApp = true;
  // 鉴定
  JidentifyContent = true;
  // 图表
  JviewDashboard = true;
  // 会话留存
  JsmDashboard = true;
  JsmChatView = true;
  JsmDataJob = true;


  constructor(public router: Router,) {
    this.routerPath = window.location.hash;
    this.sidebarMenu = [
      {
        link: 'identy',
        text: 'Identy',
        icon: 'icon-jianchajihua',
        jview: this.JidentifyContent
      },
      {
        link: 'apps',
        text: 'Apps',
        icon: 'icon-APP',
        jview: this.JmanageApp
      },
      {
        link: 'data',
        text: 'Data',
        icon: 'icon-shuju',
        jview: this.JmanageData
      },
      // {
      //   link: 'users',
      //   text: 'Users',
      //   icon: 'icon-yonghuqun',
      //   jview: this.JmanageUser
      // },
      {
        link: 'dialog',
        text: '会话留存',
        icon: 'icon-xiaoxizhongxin',
        jview: this.JsmDashboard||this.JsmChatView||this.JsmDataJob,
        isOpen: (this.routerPath.indexOf('/dialog')!==-1)?true:false,
        children: [
          {
            link: 'dialog/board',
            text: '数据看板',
            jview: this.JsmDashboard
          },
          {
            link: 'dialog/manage',
            text: '会话管理',
            jview: this.JsmChatView
          },
          {
            link: 'dialog/export',
            text: '数据导出',
            jview: this.JsmDataJob
          }
        ]
      },
    ];
  }
  ngOnInit() {
  }
}
<mat-nav-list>
  <!--循环-->
  <ng-container *ngFor="let nav of sidebarMenu">
    <!--如果需要展示-->
    <ng-container *ngIf="nav.jview">
      <!--如果子菜单是空的-->
      <mat-list-item *ngIf="!nav.children" [routerLink]="nav.link">
        <i mat-list-icon [ngClass]="['iconfont', nav.icon]"></i>
        <span mat-line>{{ nav.text}}</span>
      </mat-list-item>
      <ng-container *ngIf="nav.children">
        <mat-list-item  routerLinkActive="active" (click)="nav.isOpen = !nav.isOpen">
          <i mat-list-icon [ngClass]="['iconfont', nav.icon]"></i>
          <span mat-line>{{ nav.text + nav.isOpen }}</span>
          <ng-container *ngIf="!nav.isOpen">
            <i class="iconfont icon-right" [class.down]="nav.isOpen"></i>
          </ng-container>
          <ng-container *ngIf="nav.isOpen">
            <i class="iconfont icon-xia" [class.down]="nav.isOpen"></i>
          </ng-container>
        </mat-list-item>
        <div class="sub-nav" *ngIf="nav.isOpen" [@slideInOut]="nav.isOpen">
          <ng-container  *ngFor="let subnav of nav.children">
            <mat-list-item  *ngIf="subnav.jview" [routerLink]="subnav.link" routerLinkActive="active" >
              <!-- <i mat-list-icon [ngClass]="['iconfont', subnav.icon]"></i> -->
              <span mat-line>{{ subnav.text}}</span>
            </mat-list-item>
          </ng-container>
        </div>
      </ng-container>
    </ng-container>
  </ng-container>
</mat-nav-list>
